function criarCabecalho(){
    let cabecaHeader = document.getElementById('cabecaHeader');
    cabecaHeader.innerHTML = ' ';
    
    // Figuras Mitológicas
    let aIndex = document.createElement('a');
    aIndex.href = "./index.html";

    let souEu = document.createElement('text');
    souEu.id = "soueu";
    souEu.innerText = "Figuras Mitológicas";

    aIndex.appendChild(souEu);


    // Contato
    let aContato = document.createElement('a');
    aContato.href = "javascript:newPopup()";

    let divisorLeft = document.createElement('div');
    divisorLeft.className = "right left divisor";

    let txtContato = document.createElement('text');
    txtContato.innerText = "Contato";

    aContato.appendChild(divisorLeft);
    divisorLeft.appendChild(txtContato);


    // Deuses
    let aDeuses = document.createElement('a');
    aDeuses.href ="javascript:newPopup2()";

    let divisorRight = document.createElement('div');
    divisorRight.className = "right divisor";

    let txtDeuses = document.createElement('text');
    txtDeuses.innerText = "Deuses";

    aDeuses.appendChild(divisorRight);
    divisorRight.appendChild(txtDeuses);

    
    // Início/Sobre
    let aInicio = document.createElement('a');
    aInicio.href = "./index.html";

    let divisor = document.createElement('div');
    divisor.className = "kkpaz divisor";

    let txtInicio = document.createElement('text');
    txtInicio.innerText = "Inicio/Sobre <";
    
    aInicio.appendChild(divisor);
    divisor.appendChild(txtInicio);
    

    cabecaHeader.appendChild(aIndex);
    cabecaHeader.appendChild(aContato);
    cabecaHeader.appendChild(aDeuses);
    cabecaHeader.appendChild(aInicio);

}

let criarDivindades=(equipe)=>{

    var tituloDeEleitor = document.createElement('h1')
    tituloDeEleitor.style="text-align: center;"
    tituloDeEleitor.innerHTML = `<text>${equipe.nomeEquipe}`;
    
    var contente = document.querySelector('#contente');
    contente.innerHTML='';
    var dividendo = document.createElement('div');
    dividendo.className = "akatsuki";
    contente.appendChild(dividendo);

    dividendo.appendChild(tituloDeEleitor)

    equipe.dados.forEach(equipi=>{
        console.log(equipi);
        var divAcatsque = document.createElement('div');
        divAcatsque.className = "pain";
        divAcatsque.innerHTML= `<text style="text-align:justify;"><h1>${equipi.nomeDeus}</h1><br><h2>Resumo:</h2>${equipi.resumo}<br><br><h2>Historia Completa:</h2>${equipi.total}<br>`;
        
        var h1 = document.createElement('h1');
        h1.innerHtml=` `;
        h1.style="text-align: center;";
        var img = document.createElement('img');
        img.className = "imgine"
        img.src=equipi.src;
        dividendo.appendChild(divAcatsque);
        divAcatsque.appendChild(img);
    

}
)
    var arrumador = document.createElement('div');
    arrumador.className = "akatsuki bottom";
    contente.appendChild(arrumador);
}

let criarFooter=()=>{
    //declarações
    let footer = document.getElementById('footerbol')
    let text = document.querySelector('#mane');

    //preenchimentos
    footer.className='cabeca footer';
    text.id='soueu';
    text.innerHTML='<br>Alunos: João Lucas Paes e Henrique Claranhan';

    //childs
    footer.appendChild(text);
    
}

function carregarEquipe(equipe){
    criarCabecalho();
    criarFooter();
    criarDivindades(equipe);
    window.scroll(0,0);

}

function criarInternoCard(){    

    trabalhos.forEach( Equipe => {
        let zelda = document.createElement('a')
        console.log(Equipe)
       
        Equipe.dados.forEach(dados=>{
            dados.src+=`./ibagensDosGrupos/${Equipe.nomeEquipe}/${dados.nomeDeus}_resumo.png`;}
        )

        let internoCard = document.createElement('div')
        internoCard.className = "internoCard"

        let fonte = document.createElement('font')

        let agua = document.createElement('h2')
        agua.className = 'tituloInternoCard'
        agua.innerText = Equipe.nomeEquipe
				
      
      	let ibagem = document.createElement('img')
        ibagem.className = "cardImg"


      	var olhaOsDeuses = Equipe.dados
        olhaOsDeuses.forEach(deus => {
        	ibagem.src = deus.src;
        })

        
        otosTrabai.appendChild(zelda)
        zelda.appendChild(internoCard)
        internoCard.appendChild(fonte)
        fonte.appendChild(agua)
        internoCard.innerHTML += "<br>"
        internoCard.appendChild(ibagem)
        
        internoCard.addEventListener("click",  function(){ carregarEquipe(Equipe) })
    });
}

function carregar(){
    criarInternoCard()
}

carregar()
